import React from "react";
import { StyleProp, ViewStyle } from "react-native";
import Svg, { Path } from "react-native-svg";

type IconProps = {
  size?: number;
  color?: string;
  viewBox: string;
  children: React.ReactNode;
  onPress?: () => void;
  style?: StyleProp<ViewStyle>;
};

export function IconBase({ size, color, viewBox, onPress, style, children }: IconProps) {
  const [, , viewBoxWidth, viewBoxHeight] = viewBox.split(" ").map(Number);
  const aspectRatio = viewBoxWidth / viewBoxHeight;
  const height = size ? size / aspectRatio : undefined;

  return (
    <Svg width={size} height={height} fill={color} viewBox={viewBox} onPress={onPress} style={style}>
      {children}
    </Svg>
  );
}

